var controller;

exports.requireControllers = function () {
    var Host= require('./HostController.js'),
        News= require('./NewsController.js'),
        Page= require('./PageController.js'),
        Photo= require('./PhotoController.js'),
        User= require('./UserController.js');
    controller = {
        Host: require('./HostController.js'),
        News: require('./NewsController.js'),
        Page: require('./PageController.js'),
        Photo: require('./PhotoController.js'),
        User: require('./UserController.js')
    };
};
exports.file = function (controllerName, actionName) {
    if(!controller[controllerName]){ throw new Error ("Нет такого контроллера"); }
    return controller[controllerName].path[actionName];
};
